module.exports.default = class AppGenerator {
  constructor() {
  }

  generate(
    fs,
    workspaceContext,
    options,
    pc,
  ) {
    fs.write('src/app.module.ts', `import { Module } from '@nestjs/common'`);
  }

  getInputs() {
    return [
      {
        type: 'string',
        require: true,
        name: 'applicationName',
      },
    ];
  };
};

